import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [monthlyTotals, setMonthlyTotals] = useState(null);

    useEffect(() => {
        // Fetch the monthly totals data from the backend API
        axios.get('http://localhost:4000/api/analysis/getMonthlyTotals').then(response => {
            setMonthlyTotals(response.data);
        }).catch(error => {
            console.error('Error fetching monthly totals:', error);
        });
    }, []);

    const formatCurrency = (amount) => {
        return `$${amount.toFixed(2)}`;  // Format amount with $ sign and two decimal places
    };

    return (
        <div className="App">
            <header style={{ textAlign: 'center', padding: '20px', fontSize: '24px', fontWeight: 'bold' }}>
                Financial Expense Analysis
            </header>

            <div className="widgets-container" style={{
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'center',
                backgroundColor: '#e0f7fa',  // Light blue background
                padding: '20px',
                borderRadius: '10px'
            }}>
                <div className="widget" style={{
                    width: '500px',
                    height: 'auto',
                    margin: '10px',
                    padding: '20px',
                    backgroundColor: '#e0f7fa',  // Light blue background for the table
                    borderRadius: '10px'
                }}>
                    <h3 style={{ textAlign: 'center' }}>Monthly Totals</h3>

                    {monthlyTotals ? (
                        <table style={{ width: '100%', textAlign: 'left', borderCollapse: 'collapse' }}>
                            <tbody>
                            {Object.entries(monthlyTotals).map(([month, total]) => (
                                <tr key={month}>
                                    <td style={{
                                        fontWeight: 'bold',
                                        color: 'black',
                                        padding: '10px'
                                    }}>
                                        {month}
                                    </td>
                                    <td style={{
                                        color: 'black',
                                        padding: '10px',
                                        textAlign: 'right'
                                    }}>
                                        {formatCurrency(total)}
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    ) : (
                        'Loading...'
                    )}
                </div>
            </div>
        </div>
    );
}

export default App;
