import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [monthlyTotals, setMonthlyTotals] = useState(null);

    useEffect(() => {
        // Fetch the monthly totals data from the backend API
        axios.get('/api/analysis/getMonthlyTotals').then(response => {
            setMonthlyTotals(response.data);
        }).catch(error => {
            console.error('Error fetching monthly totals:', error);
        });
    }, []);

    const formatCurrency = (amount) => {
        return `$${amount.toFixed(2)}`;  // Format amount with $ sign and two decimal places
    };

    return (
        <div className="App">
            <header style={{ textAlign: 'center', padding: '20px', fontSize: '24px', fontWeight: 'bold' }}>
                Financial Expense Analysis
            </header>

            <div className="widgets-container" style={{
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'center',
                backgroundColor: '#e0f7fa',  // Light blue background
                padding: '20px',
                borderRadius: '10px'
            }}>
                <div className="widget" style={{
                    width: '500px',
                    margin: '10px',
                    padding: '20px',
                    backgroundColor: '#e0f7fa',  // Light blue background for the table
                    borderRadius: '10px',
                    display: 'flex',
                    flexDirection: 'column',  // Allow stacking the table and the footer
                    justifyContent: 'space-between'
                }}>
                    <div style={{
                        width: '100%',
                        maxWidth: '350px',  // Limit the maximum width of the table to avoid spreading apart
                    }}>
                        <h3 style={{ textAlign: 'center' }}>Monthly Totals</h3>

                        {monthlyTotals ? (
                            <table style={{
                                width: '100%',
                                textAlign: 'left',
                                borderCollapse: 'collapse',
                                tableLayout: 'auto',  // Allow table columns to size based on content
                                margin: '0 auto'  // Center the table horizontally
                            }}>
                                <tbody>
                                {Object.entries(monthlyTotals)
                                    .filter(([month, total]) => total > 0)  // Filter out totals less than or equal to zero
                                    .map(([month, total]) => (
                                        <tr key={month}>
                                            <td style={{
                                                fontWeight: 'bold',
                                                color: 'black',
                                                padding: '10px'
                                            }}>
                                                {month}
                                            </td>
                                            <td style={{
                                                color: 'black',
                                                padding: '10px',
                                                textAlign: 'right',
                                                whiteSpace: 'nowrap'  // Prevent the amount from breaking into a new line
                                            }}>
                                                {formatCurrency(total)}
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            'Loading...'
                        )}
                    </div>

                    {/* Footer for average monthly expense */}
                    <div style={{
                        backgroundColor: '#90caf9',  // Medium blue background
                        padding: '15px',
                        borderRadius: '8px',
                        marginTop: '20px',
                        textAlign: 'center',
                        fontWeight: 'bold',
                        color: 'black'
                    }}>
                        Average Monthly Expense: {formatCurrency(calculateAverage())}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
