import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import { Bar } from 'react-chartjs-2';

function App() {
  const [monthlyTotals, setMonthlyTotals] = useState(null);

  useEffect(() => {
    // Fetch the monthly totals data from the backend API
    axios.get('/getMonthlyTotals').then(response => {
      setMonthlyTotals(response.data);
    }).catch(error => {
      console.error('Error fetching monthly totals:', error);
    });
  }, []);

  const pieData = {
    labels: monthlyTotals ? Object.keys(monthlyTotals) : [],
    datasets: [{
      data: monthlyTotals ? Object.values(monthlyTotals) : [],
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
    }]
  };

  const barData = {
    labels: monthlyTotals ? Object.keys(monthlyTotals) : [],
    datasets: [{
      label: 'Monthly Totals',
      data: monthlyTotals ? Object.values(monthlyTotals) : [],
      backgroundColor: '#36A2EB'
    }]
  };

  return (
      <div className="App">
        <header style={{ textAlign: 'center', padding: '20px', fontSize: '24px', fontWeight: 'bold' }}>
          Financial Expense Analysis
        </header>

        <div className="widgets-container" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
          <div className="widget" style={{ width: '500px', height: '500px', margin: '10px' }}>
            <h3>Monthly Totals</h3>
            <pre>{monthlyTotals ? JSON.stringify(monthlyTotals, null, 2) : 'Loading...'}</pre>
          </div>

          <div className="widget" style={{ width: '500px', height: '500px', margin: '10px' }}>
            <h3>Monthly Totals Pie Chart</h3>
            <Pie data={pieData} />
          </div>

          <div className="widget" style={{ width: '500px', height: '500px', margin: '10px' }}>
            <h3>Monthly Totals Bar Chart</h3>
            <Bar data={barData} />
          </div>
        </div>
      </div>
  );
}

export default App;
